import React, { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

const ViewPoll = () => {
  const { id, token } = useParams();
  const location = useLocation();
  const [poll, setPoll] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [hasVoted, setHasVoted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchPoll();
  }, [id, token]); // include token

  const fetchPoll = async () => {
    try {
      setLoading(true);
      let response;
      if (token) {
        response = await axios.get(`/api/polls/invite/${token}`);
      } else {
        response = await axios.get(`/api/polls/${id}`);
      }
      setPoll(response.data.poll);
      setHasVoted(response.data.hasVoted || false);

      // Initialize answers
      const initialAnswers = {};
      response.data.poll.questions.forEach((question) => {
        initialAnswers[question._id] = null;
      });
      setAnswers(initialAnswers);
    } catch (error) {
      setError(error.response?.data?.message || "Failed to load poll");
    } finally {
      setLoading(false);
    }
  };

  const handleAnswerChange = (questionId, answer) => {
    setAnswers({
      ...answers,
      [questionId]: answer,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const formattedAnswers = Object.entries(answers).map(
        ([questionId, answer]) => ({
          questionId,
          answer,
        })
      );

      await axios.post(`/api/polls/${id}/vote`, { answers: formattedAnswers });
      setHasVoted(true);
      fetchPoll(); // Refresh to get updated results
    } catch (error) {
      setError(error.response?.data?.message || "Failed to submit vote");
    } finally {
      setSubmitting(false);
    }
  };

  const calculatePercentage = (votes, total) => {
    if (total === 0) return 0;
    return Math.round((votes / total) * 100);
  };

  // Show invite link if private poll and ?showInvite=1 in query
  const showInvite =
    poll &&
    poll.isPublic === false &&
    poll.inviteToken &&
    new URLSearchParams(location.search).get("showInvite") === "1";

  if (loading) {
    return (
      <div className="view-poll-page">
        <div className="container-sm">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading poll...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="view-poll-page">
        <div className="container-sm">
          <div className="alert alert-error">
            <i className="fas fa-exclamation-circle"></i>
            {error}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="view-poll-page">
      <div className="container-sm">
        {showInvite && (
          <div className="alert alert-info" style={{ marginBottom: 16 }}>
            <i className="fas fa-link"></i>
            Invite link:{" "}
            <a
              href={`${window.location.origin}/poll/invite/${poll.inviteToken}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {window.location.origin}/poll/invite/{poll.inviteToken}
            </a>
          </div>
        )}

        <div className="poll-header">
          <h1>{poll.title}</h1>
          {poll.description && <p>{poll.description}</p>}

          <div className="poll-meta">
            <div className="poll-creator">
              <i className="fas fa-user"></i>
              <span>Created by {poll.creator?.name || "Anonymous"}</span>
            </div>
            <div className="poll-stats">
              <span>
                <i className="fas fa-chart-bar"></i> {poll.totalResponses}{" "}
                responses
              </span>
            </div>
          </div>
        </div>

        {!hasVoted ? (
          <form onSubmit={handleSubmit} className="poll-form">
            {poll.questions.map((question, index) => (
              <div key={question._id} className="question-container">
                <h3>Question {index + 1}</h3>
                <p className="question-text">{question.question}</p>

                <div className="options-container">
                  {question.options.map((option, optionIndex) => (
                    <label key={optionIndex} className="option-label">
                      <input
                        type="radio"
                        name={question._id}
                        value={optionIndex}
                        onChange={(e) =>
                          handleAnswerChange(
                            question._id,
                            parseInt(e.target.value)
                          )
                        }
                        required={question.required}
                      />
                      <span className="option-text">{option.text}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}

            <button
              type="submit"
              className="btn btn-primary btn-lg btn-full"
              disabled={submitting}
            >
              {submitting ? (
                <>
                  <div className="spinner"></div>
                  Submitting...
                </>
              ) : (
                <>
                  <i className="fas fa-vote-yea"></i>
                  Submit Vote
                </>
              )}
            </button>
          </form>
        ) : (
          <div className="poll-results">
            <div className="results-header">
              <h2>Results</h2>
              <p>Thank you for voting! Here are the current results:</p>
            </div>

            {poll.questions.map((question, index) => (
              <div key={question._id} className="question-results">
                <h3>Question {index + 1}</h3>
                <p className="question-text">{question.question}</p>

                <div className="results-container">
                  {question.options.map((option, optionIndex) => {
                    const percentage = calculatePercentage(
                      option.votes,
                      poll.totalResponses
                    );
                    return (
                      <div key={optionIndex} className="result-option">
                        <div className="result-header">
                          <span className="option-text">{option.text}</span>
                          <span className="result-percentage">
                            {percentage}%
                          </span>
                        </div>
                        <div className="result-bar">
                          <div
                            className="result-fill"
                            style={{ width: `${percentage}%` }}
                          ></div>
                        </div>
                        <div className="result-votes">{option.votes} votes</div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ViewPoll;
