import React, { useEffect, useState } from "react";
import axios from "axios";

const GoogleSignIn = ({
  onSuccess,
  onError,
  loading,
  buttonText = "Continue with Google",
}) => {
  const [googleReady, setGoogleReady] = useState(false);
  const [hasValidClientId, setHasValidClientId] = useState(false);

  useEffect(() => {
    // Check if we have a valid Google Client ID
    const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
    const isValidClientId =
      clientId &&
      clientId !== "your_google_client_id_here" &&
      clientId !== "your-google-client-id" &&
      clientId.length > 10;

    setHasValidClientId(isValidClientId);

    // Only load Google script if we have a valid client ID
    if (isValidClientId) {
      // Load Google Identity Services script
      const script = document.createElement("script");
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;
      document.head.appendChild(script);

      script.onload = () => {
        if (window.google) {
          try {
            window.google.accounts.id.initialize({
              client_id: clientId,
              callback: handleCredentialResponse,
            });
            setGoogleReady(true);
          } catch (error) {
            console.error("Google OAuth initialization error:", error);
            setGoogleReady(false);
          }
        }
      };

      script.onerror = () => {
        console.error("Failed to load Google Identity Services");
        setGoogleReady(false);
      };

      return () => {
        // Cleanup
        const existingScript = document.querySelector(
          'script[src="https://accounts.google.com/gsi/client"]'
        );
        if (existingScript) {
          document.head.removeChild(existingScript);
        }
      };
    }
  }, []);

  const handleCredentialResponse = async (response) => {
    try {
      // Decode the JWT token to get user info
      const userInfo = parseJwt(response.credential);

      const userData = {
        credential: response.credential,
        name: userInfo.name,
        email: userInfo.email,
        picture: userInfo.picture,
      };

      // Send to backend
      const backendResponse = await axios.post("/api/auth/google", userData);

      if (onSuccess) {
        onSuccess(backendResponse.data);
      }
    } catch (error) {
      console.error("Google sign-in error:", error);
      if (onError) {
        onError(error.response?.data?.message || "Google sign-in failed");
      }
    }
  };

  const parseJwt = (token) => {
    try {
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split("")
          .map(function (c) {
            return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
          })
          .join("")
      );
      return JSON.parse(jsonPayload);
    } catch (error) {
      console.error("Error parsing JWT:", error);
      return {};
    }
  };

  const handleGoogleSignIn = () => {
    if (window.google) {
      window.google.accounts.id.prompt();
    } else {
      // Fallback to demo mode if Google SDK not loaded
      handleDemoSignIn();
    }
  };

  const handleDemoSignIn = async () => {
    try {
      const demoUserData = {
        name: "Demo Google User",
        email: "demo@gmail.com",
        picture: "https://via.placeholder.com/150",
      };

      const response = await axios.post("/api/auth/google", demoUserData);

      if (onSuccess) {
        onSuccess(response.data);
      }
    } catch (error) {
      console.error("Demo Google sign-in error:", error);
      if (onError) {
        onError(error.response?.data?.message || "Demo Google sign-in failed");
      }
    }
  };

  return (
    <button
      onClick={handleGoogleSignIn}
      className="btn btn-outline btn-full"
      disabled={loading}
    >
      {loading ? (
        <>
          <div className="spinner"></div>
          Signing in with Google...
        </>
      ) : (
        <>
          <i className="fab fa-google"></i>
          {buttonText}
        </>
      )}
    </button>
  );
};

export default GoogleSignIn;
