import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const PublicPolls = () => {
  const [polls, setPolls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [pagination, setPagination] = useState({});

  useEffect(() => {
    fetchPolls();
  }, [currentPage]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchPolls = async () => {
    try {
      setLoading(true);
      const response = await axios.get(
        `/api/polls/public?page=${currentPage}&limit=12`
      );
      setPolls(response.data.polls);
      setPagination(response.data.pagination);
    } catch (error) {
      setError("Failed to load polls");
      console.error("Error fetching polls:", error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  if (loading) {
    return (
      <div className="public-polls-page">
        <div className="container">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading polls...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="public-polls-page">
      <div className="container">
        <div className="page-header">
          <h1>Browse Public Polls</h1>
          <p>Discover and participate in polls created by the community</p>
        </div>

        {error && (
          <div className="alert alert-error">
            <i className="fas fa-exclamation-circle"></i>
            {error}
          </div>
        )}

        {polls.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">
              <i className="fas fa-poll"></i>
            </div>
            <h3>No Public Polls Yet</h3>
            <p>Be the first to create a public poll for everyone to see!</p>
            <Link to="/create" className="btn btn-primary">
              <i className="fas fa-plus"></i>
              Create First Poll
            </Link>
          </div>
        ) : (
          <>
            <div className="polls-grid">
              {polls.map((poll) => (
                <div key={poll._id} className="poll-card-item">
                  <div className="poll-card-header">
                    <h3>{poll.title}</h3>
                    {poll.description && (
                      <p className="poll-description">{poll.description}</p>
                    )}
                  </div>

                  <div className="poll-card-body">
                    <div className="poll-meta">
                      <div className="poll-creator">
                        <i className="fas fa-user"></i>
                        <span>{poll.creator?.name || "Anonymous"}</span>
                      </div>
                      <div className="poll-date">
                        <i className="fas fa-calendar"></i>
                        <span>{formatDate(poll.createdAt)}</span>
                      </div>
                    </div>

                    <div className="poll-stats">
                      <div className="stat">
                        <i className="fas fa-chart-bar"></i>
                        <span>{poll.totalResponses} responses</span>
                      </div>
                      <div className="stat">
                        <i className="fas fa-question-circle"></i>
                        <span>{poll.questions?.length || 0} questions</span>
                      </div>
                    </div>
                  </div>

                  <div className="poll-card-footer">
                    <Link
                      to={`/poll/${poll._id}`}
                      className="btn btn-primary btn-sm btn-full"
                    >
                      <i className="fas fa-vote-yea"></i>
                      View & Vote
                    </Link>
                  </div>
                </div>
              ))}
            </div>

            {/* Pagination */}
            {pagination.pages > 1 && (
              <div className="pagination">
                <button
                  onClick={() =>
                    setCurrentPage((prev) => Math.max(prev - 1, 1))
                  }
                  disabled={currentPage === 1}
                  className="btn btn-outline btn-sm"
                >
                  <i className="fas fa-chevron-left"></i>
                  Previous
                </button>

                <span className="pagination-info">
                  Page {currentPage} of {pagination.pages}
                </span>

                <button
                  onClick={() =>
                    setCurrentPage((prev) =>
                      Math.min(prev + 1, pagination.pages)
                    )
                  }
                  disabled={currentPage === pagination.pages}
                  className="btn btn-outline btn-sm"
                >
                  Next
                  <i className="fas fa-chevron-right"></i>
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default PublicPolls;
