import React, { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Dashboard = () => {
  const { user } = useAuth();
  const [myPolls, setMyPolls] = useState([]);
  const [votedPolls, setVotedPolls] = useState([]);
  const [favoritePolls, setFavoritePolls] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [myRes, votedRes, favRes] = await Promise.all([
          axios.get("/api/polls/my"),
          axios.get("/api/polls/voted"),
          axios.get("/api/polls/favorites"),
        ]);
        setMyPolls(myRes.data.polls || []);
        setVotedPolls(votedRes.data.votedPolls?.map((v) => v.poll) || []);
        setFavoritePolls(favRes.data.favoritePolls || []);
      } catch (err) {
        // handle error
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleToggleFavorite = async (pollId) => {
    try {
      const res = await axios.post(`/api/polls/${pollId}/favorite`);
      // Update favorites after toggling
      setFavoritePolls((prev) => {
        if (res.data.isFavorite) {
          // Added to favorites
          return [
            ...prev,
            myPolls.find((p) => p._id === pollId) ||
              votedPolls.find((p) => p._id === pollId),
          ];
        } else {
          // Removed from favorites
          return prev.filter((p) => p._id !== pollId);
        }
      });
    } catch (err) {
      // handle error
    }
  };

  if (loading)
    return (
      <div className="dashboard-page">
        <div className="container">
          <div className="spinner"></div>Loading...
        </div>
      </div>
    );

  return (
    <div className="dashboard-page">
      <div className="container">
        <div className="page-header">
          <h1>Welcome back, {user?.name}!</h1>
          <p>Manage your polls and view your activity</p>
        </div>

        <div className="dashboard-grid">
          <div className="dashboard-card">
            <div className="dashboard-card-header">
              <h3>My Polls</h3>
              <i className="fas fa-poll"></i>
            </div>
            <div className="dashboard-card-body">
              <div className="dashboard-stat">
                <span className="stat-number">{myPolls.length}</span>
                <span className="stat-label">Created</span>
              </div>
              <ul>
                {myPolls.map((poll) => (
                  <li key={poll._id}>
                    <Link to={`/poll/${poll._id}`}>{poll.title}</Link>
                    <button
                      onClick={() => handleToggleFavorite(poll._id)}
                      className="btn btn-link"
                    >
                      <i
                        className={`fas fa-heart${
                          favoritePolls.some((f) => f._id === poll._id)
                            ? ""
                            : "-o"
                        }`}
                      ></i>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <div className="dashboard-card">
            <div className="dashboard-card-header">
              <h3>Voted Polls</h3>
              <i className="fas fa-vote-yea"></i>
            </div>
            <div className="dashboard-card-body">
              <div className="dashboard-stat">
                <span className="stat-number">{votedPolls.length}</span>
                <span className="stat-label">Participated</span>
              </div>
              <ul>
                {votedPolls.map((poll) => (
                  <li key={poll._id}>
                    <Link to={`/poll/${poll._id}`}>{poll.title}</Link>
                    <button
                      onClick={() => handleToggleFavorite(poll._id)}
                      className="btn btn-link"
                    >
                      <i
                        className={`fas fa-heart${
                          favoritePolls.some((f) => f._id === poll._id)
                            ? ""
                            : "-o"
                        }`}
                      ></i>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <div className="dashboard-card">
            <div className="dashboard-card-header">
              <h3>Favorites</h3>
              <i className="fas fa-heart"></i>
            </div>
            <div className="dashboard-card-body">
              <div className="dashboard-stat">
                <span className="stat-number">{favoritePolls.length}</span>
                <span className="stat-label">Saved</span>
              </div>
              <ul>
                {favoritePolls.map((poll) => (
                  <li key={poll._id}>
                    <Link to={`/poll/${poll._id}`}>{poll.title}</Link>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        <div className="dashboard-section">
          <h2>Quick Actions</h2>
          <div className="quick-actions">
            <button
              className="btn btn-primary"
              onClick={() => navigate("/create")}
            >
              <i className="fas fa-plus"></i>
              Create New Poll
            </button>
            <button
              className="btn btn-outline"
              onClick={() => navigate("/polls")}
            >
              <i className="fas fa-eye"></i>
              Browse Public Polls
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
